function [res] = E002(E,phi,theta,psi)
 res=cos(phi).*cos(theta).*(cos(phi).*cos(theta).*E(1,1,3)+((-1).*cos(psi).*sin(phi)+cos(phi).*sin(psi).*sin(theta)).*E(1,2,2)+(sin(phi).*sin(psi)+cos(phi).*cos(psi).*sin(theta)).*E(2,1,2))+((-1).*cos(psi).*sin(phi)+cos(phi).*sin(psi).*sin(theta)).*(cos(phi).*cos(theta).*E(1,2,2)+((-1).*cos(psi).*sin(phi)+cos(phi).*sin(psi).*sin(theta)).*E(1,3,1)+(sin(phi).*sin(psi)+cos(phi).*cos(psi).*sin(theta)).*E(2,2,1))+(sin(phi).*sin(psi)+cos(phi).*cos(psi).*sin(theta)).*(cos(phi).*cos(theta).*E(2,1,2)+((-1).*cos(psi).*sin(phi)+cos(phi).*sin(psi).*sin(theta)).*E(2,2,1)+(sin(phi).*sin(psi)+cos(phi).*cos(psi).*sin(theta)).*E(3,1,1));
end