function [res] = E020(E,phi,theta,psi)
 res=cos(theta).*sin(phi).*(cos(theta).*sin(phi).*E(1,1,3)+(cos(phi).*cos(psi)+sin(phi).*sin(psi).*sin(theta)).*E(1,2,2)+((-1).*cos(phi).*sin(psi)+cos(psi).*sin(phi).*sin(theta)).*E(2,1,2))+(cos(phi).*cos(psi)+sin(phi).*sin(psi).*sin(theta)).*(cos(theta).*sin(phi).*E(1,2,2)+(cos(phi).*cos(psi)+sin(phi).*sin(psi).*sin(theta)).*E(1,3,1)+((-1).*cos(phi).*sin(psi)+cos(psi).*sin(phi).*sin(theta)).*E(2,2,1))+((-1).*cos(phi).*sin(psi)+cos(psi).*sin(phi).*sin(theta)).*(cos(theta).*sin(phi).*E(2,1,2)+(cos(phi).*cos(psi)+sin(phi).*sin(psi).*sin(theta)).*E(2,2,1)+((-1).*cos(phi).*sin(psi)+cos(psi).*sin(phi).*sin(theta)).*E(3,1,1));end